mapper.select:
SELECT p.productNo, p.categoryMapNo, p.shopNo, s.shopName, productName, originPrice, price, productOrigin , selStartDate, enrollDate, selEndDate, selStatus, cb.categoryName, cb.categoryNo FROM product P, categoryDetail CD, categoryBase CB, shop S WHERE 1=1 AND p.categoryMapNo = CD.categoryMapNo AND CD.categoryno = cb.categoryNo AND p.shopNo = s.shopNo
AND p.productNo = #{ productNo }
AND productName LIKE '%'||#{ productName }||'%'
AND p.categoryMapNo = #{ categoryMapNo }
AND p.shopNo = #{ shopNo }
ORDER BY productNo

SELECT p.productNo, p.categoryMapNo, p.shopNo, s.shopName, productName, originPrice, price, productOrigin , selStartDate, enrollDate, selEndDate, selStatus, cb.categoryName, cb.categoryNo FROM product P, categoryDetail CD, categoryBase CB, shop S WHERE 1=1 AND p.categoryMapNo = CD.categoryMapNo AND CD.categoryno = cb.categoryNo AND p.shopNo = s.shopNo
AND p.productNo = #{ productNo }
AND productName LIKE '%'||#{ productName }||'%'
AND p.categoryMapNo = #{ categoryMapNo }
AND p.shopNo = #{ shopNo }
