mapper.select:
SELECT orderNo, cusNo, orderDate, cusReq, orderStatus, orderBase.shopNo, shopName 
from orderBase, shop 
WHERE orderBase.shopNo=shop.shopNo AND orderDate BETWEEN #{startDate} AND #{endDate}

	AND orderNo = #{orderNo}


	AND orderBase.shopNo = #{shopNo}

SELECT orderNo, orderProduct.productNo, productCnt,productName,price,productorigin 
FROM orderproduct, product
WHERE orderproduct.productNo = product.productNo AND orderNo= #{orderNo}
